import {OAuth, OAuthEcho, OAuth2} from 'oauth';
import querystring from 'qs';
import Update from './modules/update';
import Profile from './modules/profile';
import Promise from 'promise';

export default class BufferClient {
	/**
	 * Builds the BufferClient class
	 * @param {String}  params - The access token associated with the registered app
	 * @param {Boolean} [params.authenticated] - Flag to determine whether the user has been authenticated or not. If false, a long-lived access token will be generated and assigned upon instantiation.
	 * @param {String}  params.client_id - The client ID of your Buffer application
	 * @param {String}  params.client_secret - The client secret of your Buffer application
	 * @param {String}  params.access_token - The access token for the user. If the `authentication` parameter is false, this will be overwritten by the `getAccessToken` method.
	 * @param {String}  params.redirect_url - The redirection URL for your Buffer application
	 * @param {Function} [callback] - The callback to run when the request has been fulfilled
	 * @example <caption>Simple instantiation, no authentication needed. E.g. for a user that has already authenticated their account</caption>
	 * new BufferClient({
	 *   access_token: '<your_access_token>',
	 *   client_id: '<your_client_id>',
	 *   client_secret: '<your_client_secret>',
	 *   redirect_url: '<your_redirection_url>'
	 * });
	 * @example <caption>Force access_token generation with the `authentication` flag</caption>
	 * new BufferClient({
	 *   access_token: '<your_access_token>',
	 *   client_id: '<your_client_id>',
	 *   client_secret: '<your_client_secret>',
	 *   redirect_url: '<your_redirection_url>',
	 *   authenticated: false
	 * }, function (err, res) {
	 *   // Do something here
	 * });
	 */
	constructor (params, callback = function () {}) {
		this._authenticated = typeof(params.authenticated) !== 'undefined' ? params.authenticated : true;
		this._client_id = params.client_id;
		this._client_secret = params.client_secret;
		this._access_token = params.access_token;
		this._redirect_url = params.redirect_url;
		this._api_version = '1';
		this._protocol = 'https';
		this._hostname = 'api.bufferapp.com';
		this._host = `${this._protocol}://${this._hostname}`;
		this._stringify_options = {
			arrayFormat: 'index'
		};

		/**
		 * The Buffer configuration retrieved from the /info/configuration.json endpoint
		 * @type {Object}
		 */
		this.config = {};

		/**
		 * The OAuth2 client that will be used to automatically authenticate API URLs
		 * @type {Object}
		 */
		this.client = new OAuth2(
			this._client_id,
			this._client_secret,
			`${this._host}/${this._api_version}/`,
			'oauth2/authorize',
			'oauth2/token.json',
			null
		);

		/**
		 * A promise that will return after the BufferClient instance has been built
		 * @type {Promise}
		 */
		this.promise = new Promise((resolve, reject) => {
			if (!this._authenticated) {
				// If a user hasn't authorized the client for use of their account,
				// we need to get a permanent access token before interacting with the API
				this.getAccessToken(this._access_token, (err, res) => {
					if (res) {
						this._access_token = res;
						this._authenticated = true;
						resolve();
					} else {
						reject();
					}
				});
			} else {
				resolve();
			}
		}).then(() => {
			this.getConfiguration((err, res) => {
				if (!err) {
					global._bufferAPI = this;
					this.config = res;
				}

				callback(err, res);
			});
		});
	}

	/**
	 * Queries an API endpoint using the GET method
	 * @param  {String}   endpoint - The API endpoint to query. This should not be the full API URL. Example: 'users.json'
	 * @param  {Object}   params   - A list of params to be appended to the URL. If omitted, the default Buffer API attributes will be used.
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 * @example <caption>Using the optional `params` parameter:</caption>
	 * this.get('profiles/4eb854340acb04e870000010/updates/sent.json', {
	 * 	page: 2,
	 * 	count: 100
	 * }, function (err, res) {
	 * 	// Do something
	 * });
	 * @example <caption>Omitting the optional `params` parameter</caption>
	 * this.get('profiles/4eb854340acb04e870000010/updates/sent.json', function (err, res) {
	 * 	// Do something
	 * });
	 */
	get (endpoint, params, callback) {
		if (typeof(params) === 'function') {
			callback = params;
			params = '';
		}

		if (typeof(params) === 'object') {
			params = `?${querystring.stringify(params, this._stringify_options)}`;
		}

		this.client.get(`${this._host}/${this._api_version}/${endpoint}${params}`, this._access_token, function (err, res, response) {
			if (res) {
				res = JSON.parse(res);
			}

			callback(err, res, response);
		});
	}

	/**
	 * Queries an API endpoint using the POST method
	 * @param  {String}   endpoint - The API endpoint to query. This should not be the full API URL. Example: 'users.json'
	 * @param  {Object}   params   - A list of params to be appended to the URL. If omitted, the default Buffer API attributes will be used.
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 * @example <caption>Using the optional `params` parameter:</caption>
	 * this.post('profiles/4eb854340acb04e870000010/schedules/update.json', { ... }, function (err, res) {
	 * 	// Do something
	 * });
	 * @example <caption>Omitting the optional `params` parameter</caption>
	 * this.post('user/deauthorize.json', function (err, res) {
	 * 	// Do something
	 * });
	 */
	post (endpoint, params, callback = null) {
		var post_headers = {
			'Content-Type': 'application/x-www-form-urlencoded'
		};

		if (typeof(params) === 'function') {
			callback = params;
			params   = '';
		}

		if (typeof(params) === 'object') {
			params = querystring.stringify(params, this._stringify_options);
		}

		this.client._request('POST', `${this._host}/${this._api_version}/${endpoint}`, post_headers, params, this._access_token, function (err, res, response) {
			if (res) {
				res = JSON.parse(res);
			}

			callback(err, res, response);
		});
	}

	/**
	 * Retrieves the current Buffer configuration
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 * @example
	 * // ...
	 * client.getConfiguration(function (err, res) {
	 *   // Do something
	 * });
	 */
	getConfiguration (callback) {
		this.get('info/configuration.json', callback);
	}

	/**
	 * Gets a list of profiles associated with the authenticated user
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	getProfiles (callback) {
		this.promise.then(() => {
			this.get('profiles.json', (err, res) => {
				if (!err) {
					this._profiles = {};
					async.eachSeries(res, (profile, next) => {
						this._profiles[profile.id] = new Profile(profile);
						this._profiles[profile.id].promise.then(next);
					}, function () {
						callback(err, res)
					});
				}
			});
		});
	}

	/**
	 * Returns a profile object with a given ID.
	 * Note that this does not query the API, so a list of profiles must have been retrieved beforehand.
	 * @param  {String} profile_id - The ID of the profile to retrieve
	 * @return {Object}            - The queried profile object
	 */
	getProfile (profile_id) {
		return this._profiles[profile_id];
	}

	/**
	 * Revokes access for the Buffer Client to access the API on behalf of the currently logged in user
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	deauthorizeUser (callback) {
		this.post('user/deauthorize.json', (err, res) => {
			this._profiles = {};
			this._authenticated = false;

			callback(err, res);
		});
	}

	/**
	 * Gets a permanent access token when authenticating the user.
	 * @param  {String}   access_token - The temporary access token assigned when passing through the OAuth gateway
	 * @param  {Function} callback     - The callback to run when the request has been fulfilled
	 */
	getAccessToken (access_token, callback) {
		this.client.getOAuthAccessToken(access_token, {
			redirect_uri: this._redirect_url,
			grant_type: 'authorization_code'
		}, callback);
	}

	/**
	 * Gets the authorization URL for the current app
	 * @param  {String} client_id    - The client ID you were assigned when registering your Buffer application
	 * @param  {String} redirect_url - The redirect URL you set when registering your Buffer application
	 */
	static getAuthorizationUrl (client_id, redirect_url) {
		return new OAuth2(
			client_id,
			'',
			'https://buffer.com/',
			'oauth2/authorize',
			'oauth2/token',
			null
		).getAuthorizeUrl({
			redirect_uri: redirect_url,
			response_type: 'code'
		});
	}
}

import utils from '../utils';
import Update from './update';
import async from 'async';
import Promise from 'promise';

export default class Profile {
	constructor (profile) {
		this.updates = {
			sent:    {},
			pending: {}
		}

		this.promise = new Promise((resolve, reject) => {
			if (typeof(profile) === 'string') {
				// Instantiating the update from an ID
				this.getByID(profile, function (err, res) {
					if (!err) {
						resolve(res);
					} else {
						reject(err);
					}
				});
			} else {
				// Instantiating the update from an object
				resolve(profile);
			}
		}).then((profile) => {
			// Map the profile to this object
			utils.mapObject(this, profile);
		});
	}

	get pending_updates () {
		return this.updates.pending;
	}

	set pending_updates (value) {
		this.updates.pending = value;
	}

	get sent_updates () {
		return this.updates.sent;
	}

	set sent_updates (value) {
		this.updates.sent = value;
	}

	/**
	 * Gets a single profile by its ID
	 * @param  {Integer}  profile_id - The ID of the profile to retrieve
	 * @param  {Function} callback   - The callback to run when the request has been fulfilled
	 */
	getByID (profile_id, callback) {
		_bufferAPI.get(`profiles/${profile_id}.json`, callback);
	}

	/**
	 * Gets a list of pending updates for a given profile and associates them with this object
	 * @param  {Object}   params   - API parameters to pass through to the URL
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	getPendingUpdates (params, callback) {
		if (typeof(params) === 'function') {
			callback = params;
			params   = '';
		}

		_bufferAPI.get(`profiles/${this.id}/updates/pending.json`, params, (err, res) => {
			if (!err) {
				async.forEachOf(res.updates, function (update, index, next) {
					res.updates[index] = new Update(update);
					res.updates[index].promise.then(next);
				}, (err) => {
					this.pending_updates = res.updates;
					callback(err, res);
				});
			} else {
				callback(err, res);
			}

		});
	}

	/**
	 * Gets a list of sent updates for a given profile and associates them with this object
	 * @param  {Object}   params   - API parameters to pass through to the URL
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	getSentUpdates (params, callback) {
		if (typeof(params) === 'function') {
			callback = params;
			params   = '';
		}

		_bufferAPI.get(`profiles/${this.id}/updates/sent.json`, params, (err, res) => {
			if (!err) {
				async.forEachOf(res.updates, function (update, index, next) {
					res.updates[index] = new Update(update);
					res.updates[index].promise.then(next);
				}, (err) => {
					this.sent_updates = res.updates;
					callback(err, res);
				});
			} else {
				callback(err, res);
			}
		});
	}

	/**
	 * Reorders updates in the user's buffer, given a new order
	 * @param  {Object}   params   - API parameters to pass through to the URL. "updates" must be present.
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	reorderUpdates (params, callback) {
		_bufferAPI.post(`profiles/${this.id}/updates/reorder.json`, params, callback);
	}

	/**
	 * Shuffles updates in the user's buffer at random
	 * @param {Object}   params     - The parameters to send to the API
	 * @param {Function} [callback] - The callback to run when the request has been fulfilled
	 */
	shuffleUpdates (params, callback = function () {}) {
		if (typeof(params) === 'function') {
			callback = params;
			params   = '';
		}

		_bufferAPI.post(`profiles/${this.id}/updates/shuffle.json`, params, callback);
	}

	/**
	 * Returns the schedules set for the current profile
	 * @param  {Function} [callback] - The callback to run when the request has been fulfilled
	 */
	getSchedules (callback = function () {}) {
		_bufferAPI.get(`profiles/${this.id}/schedules.json`, (err, res) => {
			if (!err) {
				this.schedules = res;
			}

			callback(err, res)
		});
	}

	/**
	 * Overwrites the schedules for the associated profile
	 * @param  {Array}    schedules  - The new schedules to send to the API
	 * @param  {Function} [callback] - The callback to run when the request has been fulfilled
	 */
	setSchedules (schedules, callback = function () {}) {
		_bufferAPI.post(`profiles/${this.id}/schedules/update.json`, {
			schedules: schedules
		}, (err, res) => {
			if (!err) {
				this.schedules = schedules;
			}

			callback(err, res)
		});
	}
}

import utils from '../utils';
import Promise from 'promise';

export default class Update {
	constructor (update) {
		this.promise = new Promise((resolve, reject) => {
			if (typeof(update) === 'string') {
				// Instantiating the update from an ID
				this.getByID(update, function (err, res) {
					resolve(res);
				});
			} else {
				// Instantiating the update from an object
				resolve(update);
			}
		}).then((update) => {
			// Map the update to this object
			utils.mapObject(this, update);
		});
	}

	/**
	 * Retrieves a singular update with a given ID
	 * @param  {Integer}  update_id - The ID of the update to retrieve
	 * @param  {Function} callback  - The callback to run when the request has been fulfilled
	 */
	getByID (update_id, callback) {
		_bufferAPI.get(`updates/${update_id}.json`, callback);
	}

	/**
	 * Updates the current status update on the API, then modifies the object to match the edit
	 * @param  {Object}   params   - The parameters to update
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	edit (params, callback) {
		this.promise.then(() => {
			_bufferAPI.post(`updates/${this.id}/update.json`, {
				text:         params.text         || this.text,
				now:          params.now          || this.now,
				media:        params.media        || this.media,
				utc:          params.utc          || false,
				scheduled_at: params.scheduled_at || this.scheduled_at
			}, (err, res) => {
				if (!err) {
					utils.mapObject(this, res.update);
				}

				callback(err, res);
			});
		});
	}

	/**
	 * Saves a status update to the user's buffer
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	save (callback = function () {}) {
		this.promise.then(() => {
			_bufferAPI.post('updates/create.json', {
				profile_ids:  this.profile_ids,
				text:         this.text,
				shorten:      this.shorten,
				now:          this.now,
				top:          this.top,
				media:        this.media,
				attachment:   this.attachment,
				scheduled_at: this.scheduled_at
			}, (err, res) => {
				if (!err && res.updates) {
					utils.mapObject(this, res.updates[0]);
				}

				callback(err, res);
			});
		});
	}

	/**
	 * Deletes a status update from the user's buffer
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	destroy (callback = function () {}) {
		this.promise.then(() => {
			_bufferAPI.post(`updates/${this.id}/destroy.json`, (err, res) => {
				if (!err) {
					// TODO:
					// There's probably a better way of doing this...
					// Ideally it wants to be scrubbed from the profile it's assigned to,
					// and the instance destroyed
					this.destroyed = true;
				}

				callback(err, res);
			});
		});
	}

	/**
	 * Instantly shares an update to the selected account
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	share (callback = function () {}) {
		_bufferAPI.post(`updates/${this.id}/share.json`, (err, res) => {
			if (!err) {
				// The update object will have changed upon sharing, so map it
				this.getByID(this.id, (err, res) => {
					if (!err) {
						utils.mapObject(this, res);
						callback(err, res);
					}
				});
			}
		});
	}

	/**
	 * Gets interactions (e.g. retweets) from an update with a given ID
	 * @param  {Object}   params   - A list of params to be appended to the URL.
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	getInteractions (event, params, callback) {
		_bufferAPI.post(`updates/${this.id}/interactions.json`, {
			event: event,
			page:  params.page  || null,
			count: params.count || null
		}, callback);
	}

	/**
	 * Moves the current update to the top of the Buffer queue
	 * @param  {Function} callback - The callback to run when the request has been fulfilled
	 */
	moveToTop (callback) {
		_bufferAPI.post(`updates/${this.id}/move_to_top.json`, callback);
	}
}

export default class Utils {
	/**
	 * Copies the object keys from one object and sets them on another
	 * @param  {Object} to   - The object to add keys to
	 * @param  {Object} from - The object to get keys from
	 */
	static mapObject (to, from) {
		for (var key in from) {
			if (from.hasOwnProperty(key)) {
				to[key] = from[key];
			}
		}
	}

	/**
	 * Shuffles an array's indexes
	 * @param  {Array} array - The array to shuffle
	 * @return {Array}       - The shuffled array
	 */
	static shuffleArray (array) {
		var counter = array.length, temp, index;

		// While there are elements in the array
		while (counter > 0) {
			// Pick a random index
			index = Math.floor(Math.random() * counter);

			// Decrease counter by 1
			counter--;

			// And swap the last element with it
			temp = array[counter];
			array[counter] = array[index];
			array[index] = temp;
		}

		return array;
	}
}

import test from 'selenium-webdriver/testing';
import express from 'express';

let client,
    driver,
    server = express();

before(function (done) {
	server.listen(3000, done);
});

after(function (done) {
	// Deauthorize the user AFTER all tests have been ran,
	// or else authentication will fail...
	client.deauthorizeUser(function (err, res) {
		should.not.exist(err);
		done();
	});
});

describe('Core Suite', function () {
	describe('Authentication', function () {
		it('should authenticate the user when returning from the OAuth gateway', function (done) {
			server.get('/authorize', function (req, res) {
				res.send(`<a id="authorize_link" href="${BufferClient.getAuthorizationUrl(app.client_id, app.redirect_url)}">Authorize</a>`);
			})
			server.get('/verify', function (req, res) {
	 			client = new BufferClient({
					access_token: req.query.code,
					client_id: app.client_id,
					client_secret: app.client_secret,
					redirect_url: app.redirect_url,
					authenticated: false
				}, function (err, res) {
					client._authenticated.should.be.true;
					client._access_token.should.not.equal(req.query.code);

					// Clear out any previously queued updates before running tests
					client.getProfiles(function (err, res) {
						var profile = client.getProfile(app.profile_id);
						profile.getPendingUpdates(function (err, res) {
							async.forEachOf(profile.pending_updates, function (update, key, next) {
								update.destroy(next);
							}, function () {
								done();
							});
						});
					});
				});
			});

			this.timeout(100000);

			driver = new seleniumWebdriver
			  .Builder()
			  .forBrowser('phantomjs')
			  .build();

			// Click through to the authorization page
			driver.get('http://localhost:3000/authorize');
			driver.findElement(By.tagName('a')).click();

			// Log in to Buffer
			driver.findElement(By.id('email')).sendKeys('buffertest@joemck.ie');
			driver.findElement(By.id('password')).sendKeys('password');
			driver.findElement(By.name('signin')).click();

			driver.findElement(By.className('allow')).click();
			driver.quit();
		});

		describe('Method: getAuthorizationUrl', function () {
			it('should include the client ID', function (done) {
				BufferClient.getAuthorizationUrl(app.client_id, app.redirect_url).should.include(app.client_id);
				done();
			});
			it('should include the redirect URI', function (done) {
				BufferClient.getAuthorizationUrl(app.client_id, app.redirect_url).should.include(encodeURIComponent(app.redirect_url));
				done();
			});
		});
	});

	describe('Instantiation', function () {
		it('should assign the authenticated flag', function (done) {
			client.should.have.property('_authenticated');
			done();
		});
		it('should assign the client ID', function (done) {
			client.should.have.property('_client_id');
			done();
		});
		it('should assign the client secret', function (done) {
			client.should.have.property('_client_secret');
			done();
		});
		it('should assign the access token', function (done) {
			client.should.have.property('_access_token');
			done();
		});
		it('should assign the redirect_url', function (done) {
			client.should.have.property('_access_token');
			done();
		});
		it('should assign the API version', function (done) {
			client.should.have.property('_api_version');
			done();
		});
		it('should assign the API protocol as HTTPS', function (done) {
			client.should.have.property('_protocol', 'https');
			done();
		});
		it('should assign the hostname as "api.bufferapp.com"', function (done) {
			client.should.have.property('_hostname', 'api.bufferapp.com');
			done();
		});
		it('should assign the host in the format "#{protocol}://#{hostname}"', function (done) {
			client.should.have.property('_host', `${client._protocol}://${client._hostname}`);
			done();
		});
		it('should set the options to be passed to querystring.stringify()', function (done) {
			client.should.have.property('_stringify_options');
			client._stringify_options.should.eql({
				arrayFormat: 'index'
			});
			done();
		});
		it('should generate the OAuth client', function(done) {
			client.should.have.property('client');
			done();
		});
		it('should retrieve the Buffer configuration', function (done) {
			client.should.have.property('config');
			done();
		});
	});

	describe('API Interaction', function () {
		describe('Method: get', function () {
			it('should respond', function (done) {
				expect(client).to.respondTo('get');
				done();
			});
			it('should not throw an error', function (done) {
				client.get('info/configuration.json', function (err, res) {
					should.not.exist(err);
					done();
				});
			});
			it('should use the GET method', function (done) {
